let lines = msg.payload.split('\n');
//node.warn(`Split input into ${lines.length} lines`)
let i = 0;
let count = lines.length;
let line = lines[0];
let output=[];
let stations = global.get('stations')||{}
let force = msg.force;

function next() {
    i = i+1;
    line = lines[i].replace(/ *<\/?td> */g,'');
    return line;
}

while (i < (count-1)) {

    //node.warn('Consider line'+line)
    //if (true) break;
    if (!line.match(/METADATA.*(Breakfast|Brisbane|Bremer|Moggill|Oxley) (R|Ck) at /)) {
        next();
        continue;
    }
    line = line.replace(/^ *<!-- *METADATA,/,'').replace(/ *--> *$/,'');
    //node.warn(`Found brisbane station [${line}]`);
    let meta = line.split(/,/);
    let payload = {
        station: meta[10].replace(/ *#$/,''),
        condition: meta[8],
        metadata: meta,
        url: `http://www.bom.gov.au/fwo/${meta[7]}/${meta[7]}.${meta[0]}.plt.shtml`
    }

//    line = unshift(lines).replace(/ *<\/?td> */g,'');
//    node.warn(`Parsing line [${line}]`);
    payload.name=next().replace(/ *#$/,'');
    payload.time=next();
    payload.level=next();
    payload.trend=next();
    //node.warn(`Station payload ${JSON.stringify(payload)}`)

    if (!force && stations[payload.station] && stations[payload.station].time==payload.time) {
        // no change
        next();
        continue;
    }
    let topic = payload.name.replace(/Brisbane R at /,'');

    node.send({topic, payload});
    //output.push({...payload});

    stations[payload.name]={...payload};

    next();   
}
global.set('stations',stations)
return(null);
