const stations = global.get('stations')||{};

const all_stations = Object.values(stations)
.filter(s=>!s.name.match(/Gregor|Devon/))
.sort((a,b)=>(Number(a.level)>Number(b.level))?-1:((Number(a.level)<Number(b.level))?1:0));
const urls = all_stations.map(
    station=>`<li><a href="${station.url}">${station.name}</a></li>`
);

msg.payload = `<ul>${urls.join("\n")}</ul>`;
return msg;

const peaks2011 = global.get('peaks2011');
const station = msg.payload.name;
if (!peaks2011[station]) return null;

msg.payload = Number(100 * Number(msg.payload.level) / peaks2011[station]).toFixed(1);
return msg;

const normal_levels = global.get('normal_levels');
const station = msg.payload.name;
const level = Number(msg.payload.level);
if (!normal_levels[station]) return null;
const normal = Number(normal_levels[station]);
//node.warn(`Normal level at ${station} is ${normal}, current is ${level}`);
msg.payload = Number( level - normal).toFixed(1);
return msg;

const peaks2022 = global.get('peaks2022');
const station = msg.payload.name;
if (!peaks2022[station]) return null;

msg.payload = Number(100 * Number(msg.payload.level) / peaks2022[station]).toFixed(1);
return msg;

